import { Image, Box, Text, Center, Icon, VStack } from "@chakra-ui/react";
import {
  FaQuestionCircle,
  FaMusic,
  FaHandPaper,
  FaThumbsUp,
} from "react-icons/fa";
import { Link } from "react-router-dom";

export default function FeatureIcon({
  src,
  alt,
  icon,
  link,
  heading,
  subheading,
}) {
  return (
    <VStack>
      <Box
        p={5}
        display={{ md: "flex" }}
        alignItems="center"
        textAlign="center"
      >
        <Center>
          <Box flexShrink={0} p={10} borderRadius="full" bg="#7f56f2">
            <Link to={link}>
              {/* <Image src={src} alt={alt} borderRadius="xl" boxSize="100px" /> */}
              <Icon borderRadius="xl" boxSize="6rem" as={icon} />
            </Link>
          </Box>
        </Center>

        <Box mt={{ base: 4, md: 0 }} ml={{ md: 6 }} textAlign="center">
          <Text
            fontWeight="bold"
            textTransform="uppercase"
            fontSize="sm"
            letterSpacing="wide"
          >
            {heading}
          </Text>
          <Text
            mt={1}
            display="block"
            fontSize="sm"
            lineHeight="normal"
            fontWeight="semibold"
          >
            {subheading}
          </Text>
        </Box>
      </Box>
    </VStack>
  );
}
