import React, { useEffect } from "react";
import Title from "../../components/heading";
import Header from "../../components/navBar";
import Menu from "../../components/menu";
import Footer from "../../components/footer";
import "./index.module.css";
import { useAuth0 } from "@auth0/auth0-react";
import { Box, useColorModeValue } from "@chakra-ui/react";
import socketIOClient from "socket.io-client";
import { config } from "../../config";
import useSocketContext from "../../context/socketContext";
const { url } = config;
const ENDPOINT = url;

const FeaturedMenu = () => {
  const data = useSocketContext();
  const socket = data[0];
  const setSocket = data[1];

  const { user } = useAuth0();
  const bg = useColorModeValue("white", "#110042");
  const color = useColorModeValue("#110042", "white");

  useEffect(() => {
    setSocket(socketIOClient(ENDPOINT));
    socket.emit("connection");
    //join room request - get name, role from auth

    return () => socket.disconnect();
  }, []);

  return (
    <Box bg={bg} color={color}>
      <Header bg={bg} color={color} />
      <Title text="Nice to see you again" user={user} />
      <Menu />
      <Footer bg={bg} color={color} />
    </Box>
  );
};

export default FeaturedMenu;
