import React, { useState } from "react";
import { Flex, Box, Button } from "@chakra-ui/react";
const advice = [
  `Don't forget about <a href="https://stackoverflow.com/c/school-of-code/questions">SOC Overflow</a>`,
  `2nd bit of advice`,
  `Another piece of advice`,
];
const random = Math.floor(Math.random() * advice.length);
console.log(random);
const Footer = () => {
  const [joke, setJoke] = useState("");
  const handleJoke = () => {
    async function getJoke() {
      const response = await fetch(
        "https://v2.jokeapi.dev/joke/Programming,Miscellaneous,Dark,Pun?blacklistFlags=nsfw,religious,racist,sexist,explicit&type=single"
      );
      let data = await response.json();
      console.log(data);
      setJoke(data.joke);
    }
    getJoke();
  };

  return (
    <Flex justify="center">
      <Box w="75%" p={10} justify="center" align="center">
        <Button mb={4} onClick={handleJoke}>
          Get Joke
        </Button>

        <h2>{joke}</h2>
      </Box>
    </Flex>
  );
};

export default Footer;
