import { useAuth0 } from "@auth0/auth0-react";
import styles from "./app.module.css";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Login from "../../pages/login/index";
import FeaturedMenu from "../../pages/featureMenu";
import Admin from "../../pages/admin";
import Menu from "../../pages/featureMenu";
import Thumb from "../../pages/thumb-o-meter";
import React, { useState, useEffect } from "react";
function App() {
  let url = process.env.REACT_APP_BACKEND_URL || "http://localhost:5000/users";

  const [role, setRole] = useState("");
  const { isAuthenticated, user } = useAuth0();
  setRole(user);
  console.log(role);
  useEffect(() => {
    async function getUsers() {
      const res = await fetch(url);
      const data = await res.json();
      console.log(data);
    }

    getUsers();
  }, [url]);

  return (
    <Router>
      <div className={styles.app}>
        <Switch>
          <Route path="/admin">
            <Admin />
          </Route>
          <Route path="/menu">
            <Menu />
          </Route>
          <Route path="/thumb">
            <Thumb />
          </Route>
          <Route path="/">
            {isAuthenticated ? <FeaturedMenu /> : <Login />}
          </Route>
        </Switch>
      </div>
    </Router>
  );
}
export default App;
