import React, { useState, useEffect } from "react";
import style from "./index.module.css";
import Thumb from "../thumb";
import CustomButton from "../../components/button";
import { ArrowBackIcon } from "@chakra-ui/icons";
import {
  Slider,
  SliderTrack,
  SliderFilledTrack,
  SliderThumb,
  CircularProgress,
  CircularProgressLabel,
} from "@chakra-ui/react";
import { Icon } from "@chakra-ui/react";
import { MdPeople } from "react-icons/md";
import Timer from "../timer/index";

function PtView({ data, submit, time, count }) {
  const [myColor, setMyColor] = useState("#2C276B");
  // display the question
  // rotatable thumb
  // slider
  // slider value
  // timer

  const [value, setValue] = useState(0);

  useEffect(() => {
    if (count > 0) {
      submit(value);
    }
  }, [value]);

  // slider.addEventListener("change", () => {
  //   sliderVal.innerText = `Value: ${slider.value}`;
  //   socket.emit("submission", { value: slider.value });
  // });
  useEffect(() => {
    if (data.outcome === 0) {
      setMyColor("#2C276B");
    } else if (data.outcome <= 33) {
      setMyColor("red");
    } else if (data.outcome > 33 && data.outcome <= 66) {
      setMyColor("orange");
    } else if (data.outcome > 66 && data.outcome <= 100) {
      setMyColor("green");
    }
  }, [data.outcome]);

  return (
    <div className={style.container} style={{ backgroundColor: myColor }}>
      <h1>{data.question || "Waiting session start"}</h1>
      <p className={style.valueInformation}>
        {" "}
        <Thumb value={value} />
        <br />
        <Slider
          aria-label="slider-ex-1"
          defaultValue={30}
          onChangeEnd={(val) => setValue(val)}
          isDisabled={count > 0 ? false : true}
        >
          <SliderTrack>
            <SliderFilledTrack />
          </SliderTrack>
          <SliderThumb />
        </Slider>
        <br />
        <p>
          Value: {value || "0"}%
          <span>
            {data.responses || "0"}/{data.participants || "0"}{" "}
            {<Icon as={MdPeople} />}
          </span>
        </p>
        <Timer count={count} time={time} />
        <CircularProgress
          value={count}
          color={count < "50%" ? "green.400" : "red"}
        >
          <CircularProgressLabel>{`${count}%`}</CircularProgressLabel>
        </CircularProgress>
        {/* <p className={style.count}>{count}</p> */}
      </p>

      <CustomButton
        className={style.backButton}
        link="/"
        icon={<ArrowBackIcon />}
        text={"Back"}
      />
    </div>
  );
}

export default PtView;
