import React, { useEffect, useState } from "react";
import style from "./index.module.css";
import NavBar from "../../components/navBar";
import AddUserForm from "../../components/addUserForm";
import UserTable from "../../components/userTable";
import SessionTable from "../../components/sessionTable";
import BackToHome from "../back-home";
import { createStandaloneToast } from "@chakra-ui/react";
import useRoleContext from "../../context/roleContext";
import { config } from "../../config";
import {
  Accordion,
  AccordionItem,
  AccordionButton,
  AccordionPanel,
  AccordionIcon,
  Box,
  Tabs,
  TabList,
  TabPanels,
  Tab,
  TabPanel,
} from "@chakra-ui/react";
import MassAlert from "../../components/massAlert";

const Admin = () => {
  // need to sort role authentication so this page only visible to coaches
  const [userTableData, setUserTableData] = useState([]);
  const [sessionTableData, setSessionTableData] = useState([]);
  const [updatePage, setUpdatePage] = useState(false);
  const result = useRoleContext();
  const role = result[0];
  const { url } = config;

  useEffect(() => {
    fetch(`${url}/users`)
      .then((response) => response.json())
      .then((payload) => setUserTableData(payload.data))
      .catch((error) => burntToast(error));
  }, [updatePage]);

  useEffect(() => {
    fetch(`${url}/session`)
      .then((response) => response.json())
      .then((payload) => setSessionTableData(payload.data))
      .catch((error) => burntToast(error));
    console.log(sessionTableData);
  }, [updatePage]);

  function deleteUser(id) {
    if (window.confirm("Are you sure?")) {
      fetch(`${url}/users/${id}`, {
        method: "DELETE",
      })
        .then((response) => response.json())
        .then((data) => {
          if (data.success === true) {
            successToast({
              name: "User Delete Success",
              message: "Successfully deleted user from the database.",
            });
            console.log("success", data.payload);
            setUpdatePage(!updatePage);
          } else {
            burntToast({
              name: "Delete User Fail",
              message: "Failed to delete user.",
            });
            console.log("Failure", data.payload);
          }
        });
    }
  }

  function deleteSession(uuid) {
    if (window.confirm("Are you sure?")) {
      fetch(`${url}/session/${uuid}`, {
        method: "DELETE",
      })
        .then((response) => response.json())
        .then((data) => {
          if (data.success === true) {
            successToast({
              name: "Session Delete Success",
              message: "Successfully deleted session from the database.",
            });
            console.log("success! Session deleted");
            setUpdatePage(!updatePage);
          } else {
            burntToast({
              name: "Delete Session Fail",
              message: "Failed to delete session",
            });
            console.log("Failure!");
          }
        });
    }
  }
  function successToast(successObject) {
    const toast = createStandaloneToast();
    toast({
      title: successObject.name,
      description: successObject.message,
      status: "success",
      duration: 9000,
      isClosable: true,
    });
  }

  function burntToast(error) {
    const toast = createStandaloneToast();
    toast({
      title: error.name,
      description: error.message,
      status: "error",
      duration: 10000,
      isClosable: true,
    });
    console.log(error);
  }

  return (
    <>
      <NavBar />
      {role === "coach" ? (
        <main className={style.main}>
          <Accordion className={style.accordion} allowToggle>
            <AccordionItem>
              <AccordionButton>
                <Box flex="1" textAlign="left">
                  Add User Form
                </Box>
                <AccordionIcon />
              </AccordionButton>
              <AccordionPanel pb={4}>
                <AddUserForm
                  updatePage={updatePage}
                  setUpdatePage={setUpdatePage}
                />
              </AccordionPanel>
            </AccordionItem>

            <AccordionItem>
              <AccordionButton>
                <Box flex="1" textAlign="left">
                  Send App Message
                </Box>
                <AccordionIcon />
              </AccordionButton>
              <AccordionPanel pb={4}>
                <MassAlert />
              </AccordionPanel>
            </AccordionItem>

            <AccordionItem>
              <AccordionButton>
                <Box flex="1" textAlign="left">
                  Send App Message
                </Box>
                <AccordionIcon />
              </AccordionButton>
              <AccordionPanel pb={4}>
                <MassAlert />
              </AccordionPanel>
            </AccordionItem>
          </Accordion>

          <Tabs className={style.tab}>
            <TabList>
              <Tab>Users</Tab>
              <Tab>Sessions</Tab>
            </TabList>

            <TabPanels>
              <TabPanel>
                <div className={style.userTable}>
                  <UserTable
                    tableData={userTableData}
                    deleteUser={deleteUser}
                  />
                </div>
              </TabPanel>
              <TabPanel>
                <div className={style.sessionTable}>
                  <SessionTable
                    tableData={sessionTableData}
                    deleteSession={deleteSession}
                  />
                </div>
              </TabPanel>
            </TabPanels>
          </Tabs>
        </main>
      ) : (
        <BackToHome />
      )}
    </>
  );
};
export default Admin;
