import React, { useState } from "react";
import { Flex, Box, Button, Input } from "@chakra-ui/react";

const Footer = () => {
  const [joke, setJoke] = useState("");
  const [value, setValue] = useState("");
  const [advice, setAdvice] = useState([[]]);
  const handleChange = (event) => setValue(event.target.value);
  const handleJoke = () => {
    async function getJoke() {
      const response = await fetch(
        "https://v2.jokeapi.dev/joke/Programming,Miscellaneous,Dark,Pun?blacklistFlags=nsfw,religious,racist,sexist,explicit&type=single"
      );
      let data = await response.json();

      setJoke(data.joke);
    }
    getJoke();
  };
  setAdvice([
    <Button>
      <a
        href="https://stackoverflow.com/c/school-of-code/questions"
        target="_blank"
        rel="noreferrer"
      >
        Don't Forget about SOC Overflow
      </a>
    </Button>,

    <Input
      value={value}
      onChange={handleChange}
      placeholder="Here is a sample placeholder"
      size="sm"
    />,

    <Button>
      <a
        href={`https://google.co.uk/search.php?q=${value}`}
        target="_blank"
        rel="noreferrer"
      >
        Don't Forget about SOC Overflow
      </a>
    </Button>,
  ]);
  const random = Math.floor(Math.random() * advice.length);
  console.log(random);
  return (
    <Flex justify="center">
      <Box w="75%" p={10} justify="center" align="center">
        <Button mb={4} onClick={handleJoke}>
          Get Joke
        </Button>

        <p>{joke}</p>
        <span>{advice[0]}</span>
      </Box>
    </Flex>
  );
};

export default Footer;
