import React, { useState, createContext, useContext, useEffect } from "react";
import { useAuth0 } from "@auth0/auth0-react";

const RoleContext = createContext("guest");

export function RoleProvider({ children }) {
  const [role, setRole] = useState("guest");
  const { user } = useAuth0();
  const [loggedUser, setLoggedUser] = useState("");
  const [result, setResult] = useState("");

  useEffect(() => {
    async function getUsers() {
      setLoggedUser(user);
      const data = await fetch(`https://callback-cats.herokuapp.com/users`);
      const response = await data.json();
      setResult(response);
    }
    getUsers();

    //loggedUser is the Auth0 information
  }, [loggedUser, role, user, result.data]);

  return (
    <RoleContext.Provider value={[result, loggedUser]}>
      {children}
    </RoleContext.Provider>
  );
}

export default function useRoleContext() {
  return useContext(RoleContext);
}
