import React from "react";
import FeatureIcon from "../featureIcons";
import styles from "./menu.module.css";
import useRoleContext from "../../context/roleContext";
import { features, coachFeatures } from "./data";
import {
  FaQuestionCircle,
  FaMusic,
  FaHandPaper,
  FaThumbsUp,
} from "react-icons/fa";

const Menu = () => {
  const result = useRoleContext();
  const role = result[0];
  console.log(features);
  return (
    <main>
      <div className={styles.container}>
        <div className={styles.players}>
          {features.map((item) => {
            return (
              <section>
                <FeatureIcon
                  alt={item.alt}
                  src={item.src}
                  link={item.link}
                  icon={item.icon}
                  heading={item.heading}
                />
              </section>
            );
          })}
          {role === "coach" && (
            <section>
              {" "}
              <FeatureIcon
                alt="djDeck"
                src="/music.png"
                link="/deck"
                icon={FaMusic}
                heading="DJ Deck"
                subheading="A little bit of music you say?"
              />
            </section>
          )}
        </div>
      </div>
    </main>
  );
};

export default Menu;
