import React, { useEffect } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Title from "../../components/heading";
import Header from "../../components/navBar";
import Menu from "../../components/menu";
import Footer from "../../components/footer";
import "./index.module.css";
import { useAuth0 } from "@auth0/auth0-react";
import { Box, useColorModeValue } from "@chakra-ui/react";
import Admin from "../../pages/admin";
import RaiseHand from "../../pages/raise-hand";
import Quiz from "../../pages/quiz";
import Deck from "../../pages/dj-deck";
import Thumbometer from "../../pages/thumb-o-meter";
import Unauthorised from "../../pages/unauthorised";
import useSocketContext from "../../context/socketContext";

const FeaturedMenu = ({ role }) => {
  const data = useSocketContext();
  const socket = data[0];

  const { user } = useAuth0();
  const bg = useColorModeValue("white", "#110042");
  const color = useColorModeValue("#110042", "white");

  useEffect(() => {
    socket.emit("connection");

    //return () => socket.disconnect();
  }, []);

  return (
    <Router>
      {" "}
      <Box bg={bg} color={color}>
        <Header bg={bg} color={color} />
        <Title text="Nice to see you again" user={user} />
        <Menu role={role} />
        <Footer bg={bg} color={color} />
      </Box>
      <Switch>
        <Route path="/admin">
          <Admin />
        </Route>
        <Route path="/unauthorised">
          <Unauthorised />
        </Route>
        <Route path="/raisehand">
          <RaiseHand />
        </Route>
        <Route path="/quiz">
          <Quiz />
        </Route>
        <Route path="/deck">
          <Deck user={user} />
        </Route>
        <Route path="/thumb">
          <Thumbometer />
        </Route>
      </Switch>
    </Router>
  );
};

export default FeaturedMenu;
