import React, { useState } from "react";
import { Link } from "react-router-dom";
import LogoutButton from "../logout/index";
import { Box, Flex, Text, Button, useColorModeValue } from "@chakra-ui/react";
import ThemeToggler from "../theme";
import { HamburgerIcon, CloseIcon } from "@chakra-ui/icons";
import styles from "./navBar.module.css";

const MenuItems = ({ children }) => (
  <Text className={styles.menu}>{children}</Text>
);

const Header = ({ role }) => {
  const [show, setShow] = useState(false);
  const toggleMenu = () => setShow(!show);

  const bg = useColorModeValue("#7f56f2", "#110042");
  const color = useColorModeValue("#110042", "white");
  return (
    <Flex //className = {styles.container}
      as="nav"
      bg={bg}
      color={color}
    >
      {/* <Icon as={FaCat} /> */}
      <ThemeToggler />
      <Box display={{ base: "block", md: "none" }} onClick={toggleMenu}>
        {show ? <CloseIcon /> : <HamburgerIcon />}
      </Box>

      <Box
        display={{ base: show ? "block" : "none", md: "block" }}
        flexBasis={{ base: "100%", md: "auto" }}
      >
        <Flex
          align={["center", "center", "center", "center"]}
          justify={["center", "center", "space-between", "space-between"]}
          direction={["column", "row", "row", "row"]}
          pt={[4, 4, 0, 0]}
        >
          <MenuItems>
            <Link to="/thumb">Thumb-o-meter</Link>
          </MenuItems>
          <MenuItems>
            <Link to="/raisehand">Raise Hand</Link>
          </MenuItems>
          <MenuItems>
            <Link to="/quiz">Live Quiz</Link>
          </MenuItems>
          {role === "coach" && (
            <>
              <MenuItems>
                <Link to="/deck">DJ Deck</Link>
              </MenuItems>
              <MenuItems>
                <Link to="/admin">Admin</Link>
              </MenuItems>
            </>
          )}
          <Button
            size="sm"
            rounded="md"
            ml={2}
            bg={bg}
            mb={2}
            _hover={{
              bg: ["primary.100", "primary.100", "primary.600", "primary.600"],
            }}
          >
            <LogoutButton />
          </Button>
        </Flex>
      </Box>
    </Flex>
  );
};

export default Header;
