import React, { useState } from "react";
import { Link } from "react-router-dom";
import {
  Box,
  Heading,
  Flex,
  Text,
  useColorMode,
  useColorModeValue,
  Button,
} from "@chakra-ui/react";
import { HamburgerIcon } from "@chakra-ui/icons";
import LogoutButton from "../logout/index";
const MenuItems = ({ children }) => (
  <Text mt={{ base: 4, md: 0 }} mr={6} display="block">
    {children}
  </Text>
);

// Note: This code could be better, so I'd recommend you to understand how I solved and you could write yours better :)
const Header = ({ role }) => {
  const [show, setShow] = useState(false);
  const handleToggle = () => setShow(!show);
  const { colorMode, toggleColorMode } = useColorMode();
  const bg = useColorModeValue("#7f56f2", "#110042");
  const color = useColorModeValue("black", "white");

  return (
    <Flex
      as="nav"
      align="center"
      justify="space-between"
      wrap="wrap"
      padding="1.5rem"
      bg={bg}
      color={color}
    >
      <Flex align="center" mr={5}>
        <Heading as="h1" size="lg">
          <Link to="/">Logo Here</Link>
        </Heading>
      </Flex>

      <Box
        display={{ base: "block", md: "none" }}
        mr={5}
        onClick={handleToggle}
      >
        <HamburgerIcon />
      </Box>

      <Box
        display={{ sm: show ? "block" : "none", md: "flex" }}
        width={{ sm: "full", md: "auto" }}
        alignItems="center"
        flexGrow={1}
      >
        {" "}
        <Button size="sm" mr={5} mt={2} mb={2} onClick={toggleColorMode}>
          Toggle Mode
        </Button>
        <MenuItems>
          <Link to="/thumb">Thumb-o-meter</Link>
        </MenuItems>
        <MenuItems>
          <Link to="/raisehand">Raise Hand</Link>
        </MenuItems>
        <MenuItems>
          <Link to="/quiz">Live Quiz</Link>
        </MenuItems>
        {role === "coach" && (
          <>
            <MenuItems>
              <Link to="/deck">DJ Deck</Link>
            </MenuItems>
            <MenuItems>
              <Link to="/admin">Admin</Link>
            </MenuItems>
          </>
        )}
      </Box>

      <Box
        display={{ sm: show ? "block" : "none", md: "block" }}
        mt={{ base: 4, md: 0 }}
      >
        <Box bg="transparent" border="1px" p={2} borderRadius="lg">
          <LogoutButton />
        </Box>
      </Box>
    </Flex>
  );
};

export default Header;
