import { Box, Text, Center, Icon, VStack } from "@chakra-ui/react";
import { Link } from "react-router-dom";
import styles from "./featureIcons.module.css";

export default function FeatureIcon({ icon, link, heading, subheading }) {
  return (
    <VStack className={styles.container}>
      <Center>
        <Box flexShrink={0} p={10} borderRadius="full" bg="#7f56f2">
          <Link to={link}>
            {/* <Image src={src} alt={alt} borderRadius="xl" boxSize="100px" /> */}
            <Icon
              borderRadius="xl"
              boxSize={{ base: "2rem", md: "4rem", lg: "6rem" }}
              as={icon}
              color="white"
            />
          </Link>
        </Box>
      </Center>
      <Box mt={{ base: 4, md: 0 }} ml={{ md: 6 }} textAlign="center">
        <Text
          fontWeight="bold"
          textTransform="uppercase"
          fontSize="sm"
          letterSpacing="wide"
        >
          {heading}
        </Text>
        <Text
          mt={1}
          display="block"
          fontSize="sm"
          lineHeight="normal"
          fontWeight="semibold"
        >
          {subheading}
        </Text>
      </Box>
    </VStack>
  );
}
