import React, { useEffect } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import FeaturedMenu from "../../pages/featureMenu";
import Unauthorised from "../unauthorised";
import useRoleContext from "../../context/roleContext";
import { config } from "../../config";

import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Login from "../../pages/login/index";
import Admin from "../../pages/admin";
import RaiseHand from "../../pages/raise-hand";
import Quiz from "../../pages/quiz";
import Deck from "../../pages/dj-deck";
import Thumbometer from "../../pages/thumb-o-meter";

const envUrl = config.url;
const Authorised = () => {
  const data = useRoleContext();
  const role = data[0];
  const setRole = data[1];
  const setLoggedUser = data[3];
  // const loggedUser = data[2];

  // console.log(myRole[2]);
  // const [role, setRole] = useState("");

  const { user } = useAuth0();
  console.log(user);
  // const [loggedUser, setLoggedUser] = useState(user);

  let url = `${envUrl}/users/${user.email}`;

  useEffect(() => {
    async function getUsers() {
      setLoggedUser(user);
      const data = await fetch(url);
      const result = await data.json();
      console.log(result.success);
      result.success === false
        ? setRole("unauthorised")
        : setRole(result.data.role);
      console.log(role);
    }

    getUsers();

    //loggedUser is the Auth0 information
  }, []);
  return (
    <div>
      {role === "unauthorised" ? (
        <Unauthorised />
      ) : (
        <FeaturedMenu role={role} />
      )}
    </div>
  );
};

export default Authorised;

<Router>
  <div className={styles.app}>
    <Switch>
      <Route path="/admin">
        <Admin />
      </Route>
      <Route path="/unauthorised">
        <Unauthorised />
      </Route>
      <Route path="/raisehand">
        <RaiseHand />
      </Route>
      <Route path="/quiz">
        <Quiz />
      </Route>
      <Route path="/deck">
        <Deck user={user} />
      </Route>
      <Route path="/thumb">
        <Thumbometer />
      </Route>
      <Route path="/">{isAuthenticated ? <Authorised /> : <Login />}</Route>
    </Switch>
  </div>
</Router>;
