import React from "react";
import { Center, Button, Box, HStack } from "@chakra-ui/react";
import FeatureIcon from "../featureIcons";
import styles from "./menu.module.css";
import {
  FaQuestionCircle,
  FaMusic,
  FaHandPaper,
  FaThumbsUp,
} from "react-icons/fa";
import { Link } from "react-router-dom";
const Menu = ({ role }) => {
  return (
    <Center>
      <Box className={styles.container}>
        <Center>
          {" "}
          <FeatureIcon
            alt="thumbometer"
            src="/thumb.png"
            link="/thumb"
            icon={FaThumbsUp}
            heading="Thumb-o-meter"
            subheading="Hows it going?"
          />
          <FeatureIcon
            alt="raisehand"
            src="/raisehand.jpeg"
            link="/raisehand"
            icon={FaHandPaper}
            heading="Raise a Hand"
            subheading="Got a question?"
          />
          <FeatureIcon
            alt="livequiz"
            src="/quizzical.jpeg"
            link="/quiz"
            icon={FaQuestionCircle}
            heading="Live Quiz"
            subheading="How much do you understand?"
          />
          {role === "coach" && (
            <>
              <FeatureIcon
                alt="djDeck"
                src="/dj-deck.png"
                link="/deck"
                icon={FaMusic}
                heading="DJ Deck"
                subheading="A little bit of music you say?"
              />
              <Button>
                <Link to="/admin">Admin</Link>
              </Button>{" "}
            </>
          )}
        </Center>
      </Box>
    </Center>
  );
};

export default Menu;
