import React from "react";
import { Button, Center, VStack } from "@chakra-ui/react";
import FeatureIcon from "../featureIcons";
import styles from "./menu.module.css";
import { Route, Switch } from "react-router-dom";
import Admin from "../../pages/admin";
import RaiseHand from "../../pages/raise-hand";
import Quiz from "../../pages/quiz";
import Deck from "../../pages/dj-deck";
import Thumb from "../../pages/thumb-o-meter";
import {
  FaQuestionCircle,
  FaMusic,
  FaHandPaper,
  FaThumbsUp,
} from "react-icons/fa";

const Menu = ({ role, user }) => {
  return (
    <>
      <div className={styles.container}>
        <div className={styles.players}>
          <section>
            {" "}
            <FeatureIcon
              alt="thumbometer"
              src="/thumb.png"
              link="/thumb"
              icon={FaThumbsUp}
              heading="Thumb-o-meter"
              subheading="Hows it going?"
            />
          </section>
          <section>
            {" "}
            <FeatureIcon
              alt="raisehand"
              src="/raisehand.png"
              link="/raisehand"
              icon={FaHandPaper}
              heading="Raise a Hand"
              subheading="Got a question?"
            />
          </section>
          <section>
            {" "}
            <FeatureIcon
              alt="livequiz"
              src="/quizzical.png"
              link="/quiz"
              icon={FaQuestionCircle}
              heading="Live Quiz"
              subheading="How much do you understand?"
            />
          </section>

          {role === "coach" && (
            <section>
              {" "}
              <FeatureIcon
                alt="djDeck"
                src="/music.png"
                link="/deck"
                icon={FaMusic}
                heading="DJ Deck"
                subheading="A little bit of music you say?"
              />
            </section>
          )}
        </div>
      </div>

      <Switch>
        <Route path="/admin">
          <Admin />
        </Route>
        <Route path="/raisehand">
          <RaiseHand />
        </Route>
        <Route path="/quiz">
          <Quiz />
        </Route>
        <Route path="/deck">
          <Deck user={user} />
        </Route>
        <Route path="/thumb">
          <Thumb />
        </Route>
      </Switch>
    </>
  );
};

export default Menu;
